import React, { useState } from "react";
import Modal from "./Modal";

function Account() {
  // Sample data to demonstrate the table
  const initialData = {
    id: 1,
    first_name: "John",
    last_name: "Doe",
    username: "johndoe",
    email: "john@example.com",
    age: 30,
    location: "New York",
    gender: "Male",
    seeking_gender: "Female",
    marital_status: "Single",
    bio: "Lorem ipsum dolor sit amet...",
    interest: "Hiking, Photography",
    height: "6'2\"",
    ethinicity: "Caucasian",
    living_with: "Alone",
    education_level: "Bachelor's Degree",
    no_of_children: "0",
    drinking_habits: "Social Drinker",
    smoking_habits: "Non-Smoker",
    passion: "Traveling, Music",
    account_status: "Active",
    user_id: 101,
    created_at: "2023-07-23 12:34:56",
  };

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [formData, setFormData] = useState(initialData);

  const handleEditClick = () => {
    setIsModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Add your form submission logic here (e.g., API call to update data)
    console.log(formData);
    setIsModalOpen(false); // Close the modal after submitting
  };

  return (
    <div className="account">
      <table>
        <tbody>
          <tr>
            <td>ID:</td>
            <td>{formData.id}</td>
          </tr>
          <tr>
            <td>First Name:</td>
            <td>{formData.first_name}</td>
          </tr>
          <tr>
            <td>Last Name:</td>
            <td>{formData.last_name}</td>
          </tr>
          <tr>
            <td>Username:</td>
            <td>{formData.username}</td>
          </tr>
          <tr>
            <td>Email:</td>
            <td>{formData.email}</td>
          </tr>
          {/* Render other table rows similarly */}
          {/* ... */}
        </tbody>
      </table>

      <button onClick={handleEditClick}>Edit</button>

      <Modal isOpen={isModalOpen} onClose={handleModalClose}>
        <h2>Edit Details</h2>
        <form onSubmit={handleFormSubmit}>
          <label>
            ID:
            <input type="text" name="id" value={formData.id} readOnly />
          </label>
          <label>
            First Name:
            <input
              type="text"
              name="first_name"
              value={formData.first_name}
              onChange={handleChange}
            />
          </label>
          <label>
            Last Name:
            <input
              type="text"
              name="last_name"
              value={formData.last_name}
              onChange={handleChange}
            />
          </label>
          {/* Render other input fields similarly */}
          {/* ... */}
          <button type="submit">Save</button>
          <button onClick={handleModalClose}>Cancel</button>
        </form>
      </Modal>
    </div>
  );
}

export default Account;
