import React, { useState } from "react";
import { Link } from "react-router-dom";


function Signup() {
  const [gender, setGender] = useState("male");
  const [seeking, setSeeking] = useState("female");

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };

  const handleSeekingChange = (event) => {
    setSeeking(event.target.value);
  };

  return (
    <div className="formwrapper">
      <h1>FLIRTOPIA</h1>
      <h3>FILL IN YOUR INFO</h3>
      <form>
        {/* Other input fields here */}

        {/* Age Field */}
        <label>Date of Birth</label>
        <input type="date" name="dob" max={getMaxDate()} />

        {/* Gender Selection */}
        <label>I am</label>
        <select name="gender" value={gender} onChange={handleGenderChange}>
          <option value="male">Male</option>
          <option value="female">Female</option>
        </select>

        {/* Seeking Gender Selection */}
        <label>I am seeking</label>
        <select name="seeking" value={seeking} onChange={handleSeekingChange}>
          <option value="male">Male</option>
          <option value="female">Female</option>
        </select>

        {/* Rest of the form */}
      </form>
      <p>
        By creating an account you agree to our{" "}
        {/* <a href="#" style={{ color: "dodgerblue" }}> Terms & Privacy</a> */}
      </p>
      <button type="submit">Signup</button>
      <p>
        Have an account? Then Sign in <Link to="/signup">Signup</Link>.
      </p>
    </div>
  );
}

// Helper function to calculate the maximum date for the date picker (18 years ago from the current date)
function getMaxDate() {
  const today = new Date();
  const maxDate = new Date(
    today.getFullYear() - 18,
    today.getMonth(),
    today.getDate()
  );
  return maxDate.toISOString().split("T")[0];
}

export default Signup;
