import React, { useState } from "react";
import ProfileCard from "./ProfileCard";

function Account() {
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    username: "",
    email: "",
    age: "",
    location: "",
    gender: "",
    seeking_gender: "",
    marital_status: "",
    bio: "",
    interest: "",
    ethnicity: "",
    living_with: "",
    education_level: "",
    no_of_children: "",
    drinking_habits: "",
    smoking_habits: "",
    passion: "",
    account_status: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your form submission logic here
    console.log(formData);
  };

  return (
    <div className="account">
      <form onSubmit={handleSubmit}>
        <label>
          First Name:
          <input
            type="text"
            name="first_name"
            value={formData.first_name}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Last Name:
          <input
            type="text"
            name="last_name"
            value={formData.last_name}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          username:
          <input
            type="text"
            name="username"
            value={formData.last_name}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          email:
          <input
            type="text"
            name="Email"
            value={formData.last_name}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Age:
          <input
            type="text"
            name="age"
            value={formData.last_name}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          location:
          <input
            type="text"
            name="Location"
            value={formData.last_name}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          gender:
          <input
            type="text"
            name="gender"
            value={formData.last_name}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          seeking_gender:
          <input
            type="text"
            name="seeking_gender"
            value={formData.last_name}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
           living_with:
          <input
            type="text"
            name="living_with"
            value={formData.last_name}
            onChange={handleChange}
          />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Account;
